mapper.select:
SELECT ANN_NUM , ANN_CATE , CATE_NAME , ANN_WRITER , ANN_TITLE , ANN_CONTENT , TO_CHAR(ANN_DATE,'YYYY-MM-DD') AS ANN_DATE FROM ANNOUNCEMENT ANN, ANN_CATE_LIST LIST WHERE ANN.ANN_CATE = LIST.CATE_CODE
AND ANN_CATE = #{annCate}
ORDER BY ANN_NUM DESC OFFSET (#{nowPage} -1) * #{displayDataCnt} ROWS FETCH FIRST #{displayDataCnt} ROWS ONLY

mapper.insert:
INSERT INTO ANNOUNCEMENT (
    ANN_NUM
    , ANN_CATE
    , ANN_TITLE
    , ANN_CONTENT
)
VALUES (
    (SELECT 'ANN_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ANN_NUM, 5))),0) + 1, 3, '0')
    FROM ANNOUNCEMENT)
    , #{annCate}
    , #{annTitle}
    , #{annContent}
)

mapper.update:
UPDATE ANNOUNCEMENT
SET
    ANN_CATE = #{annCate}
    , ANN_TITLE = #{annTitle}
    , ANN_CONTENT = #{annContent}
WHERE ANN_NUM = #{annNum}

mapper.insert:
INSERT INTO QNA (
QNA_CODE
, CATE_CODE
, QNA_QUESTION
, QNA_ANSWER
)
VALUES (
(SELECT 'QNA_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(QNA_CODE, 5))),0) + 1, 3, '0')
FROM QNA)
, #{cateCode}
, #{qnaQuestion}
, #{qnaAnswer}
)

mapper.update:
UPDATE QNA
SET
    CATE_CODE = #{cateCode}
    , QNA_QUESTION = #{qnaQuestion}
    , QNA_ANSWER = #{qnaAnswer}
WHERE QNA_CODE = #{qnaCode}

mapper.insert:
INSERT INTO INQUIRE (
INQ_CODE
, CATE_CODE
, TITLE
, CONTENT
)
VALUES (
(SELECT 'INQ_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(INQ_CODE, 5))),0) + 1, 3, '0')
FROM INQUIRE)
, #{cateCode}
, #{title}
, #{content}
)

INSERT INTO INQ_IMG (
    IMG_CODE
    , ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , INQ_CODE
)

    SELECT (SELECT 'IMG_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(IMG_CODE, 5))),0) + 1 + #{idx}, 3, '0')
    FROM INQ_IMG)
    , #{inqImg.originFileName}
    , #{inqImg.attachedFileName}
    , #{inqImg.inqCode}
    FROM DUAL
